import React, { useState, useEffect } from 'react';

const SignSharedFile = ({ token }) => {
    const [sharedFiles, setSharedFiles] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [privateKeyFile, setPrivateKeyFile] = useState(null);
    const [signature, setSignature] = useState(null);

    useEffect(() => {
        fetchSharedFiles();
    }, []);

    const fetchSharedFiles = async () => {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/files/user-files`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });
            const files = await response.json();
            if (Array.isArray(files)) {
                setSharedFiles(files);
            } else {
                console.error('Error: La respuesta no es un arreglo.', files);
            }
        } catch (error) {
            console.error('Error al obtener los archivos compartidos:', error);
        }
    };

    const handleFileSelect = (file) => {
        setSelectedFile(file);
    };

    const handlePrivateKeyChange = (event) => {
        setPrivateKeyFile(event.target.files[0]);
    };

    const handleSignFile = async () => {
        if (!selectedFile || !privateKeyFile) {
            alert('Por favor selecciona un archivo y una llave privada');
            return;
        }

        const formData = new FormData();
        formData.append('archivo_id', selectedFile.id);
        formData.append('privateKey', privateKeyFile);

        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/files/sign-shared`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
                body: formData,
            });

            const result = await response.json();
            if (result.signature) {
                setSignature(result.signature);
                alert('Archivo firmado exitosamente');
            } else {
                alert('Error al firmar el archivo');
            }
        } catch (error) {
            console.error('Error al firmar el archivo:', error);
            alert('Error al firmar el archivo');
        }
    };

    return (
        <div>
            <h2>Firmar Archivos Compartidos</h2>
            <select onChange={(e) => handleFileSelect(sharedFiles[e.target.value])}>
                <option value="">Selecciona un archivo</option>
                {sharedFiles.map((file, index) => (
                    <option key={file.id} value={index}>
                        {file.nombre_archivo}
                    </option>
                ))}
            </select>

            <input
                type="file"
                accept=".pem"
                onChange={handlePrivateKeyChange}
            />

            <button onClick={handleSignFile}>Firmar Archivo</button>

            {signature && (
                <div>
                    <h3>Firma generada:</h3>
                    <pre>{signature}</pre>
                </div>
            )}
        </div>
    );
};

export default SignSharedFile;
