import React, { useState, useEffect } from 'react';

const SignSharedFile = ({ token }) => {
    const [sharedFiles, setSharedFiles] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [privateKeyFile, setPrivateKeyFile] = useState(null);
    const [message, setMessage] = useState('');

    // Obtener archivos compartidos
    const fetchSharedFiles = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/files/shared-files', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                throw new Error('Error al obtener archivos compartidos.');
            }

            const data = await response.json();
            setSharedFiles(data);
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        fetchSharedFiles();
    }, [token]);

    const handleFileChange = (e) => {
        setPrivateKeyFile(e.target.files[0]);
    };

    const handleSign = async () => {
        if (!selectedFile || !privateKeyFile) {
            setMessage('Por favor selecciona un archivo y sube tu llave privada (.pem).');
            return;
        }

        const formData = new FormData();
        formData.append('shared_id', selectedFile);
        formData.append('privateKeyFile', privateKeyFile);

        try {
            const response = await fetch('http://localhost:5000/api/files/sign-shared', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
                body: formData,
            });

            const data = await response.json();
            setMessage(data.message || data.error);
        } catch (error) {
            console.error('Error al firmar archivo compartido:', error);
            setMessage('Error al firmar el archivo compartido.');
        }
    };

    return (
        <div>
            <h2>Firmar Archivos Compartidos</h2>
            <div>
                <label>Seleccionar Archivo:</label>
                <select onChange={(e) => setSelectedFile(e.target.value)} value={selectedFile}>
                    <option value="">-- Selecciona un archivo --</option>
                    {sharedFiles.map(file => (
                        <option key={file.shared_id} value={file.shared_id}>
                            {file.nombre_archivo} (Tama√±o: {file.tamano} bytes)
                        </option>
                    ))}
                </select>
            </div>
            <div>
                <label>Subir Llave Privada (.pem):</label>
                <input type="file" accept=".pem" onChange={handleFileChange} />
            </div>
            <button onClick={handleSign}>Firmar Archivo</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default SignSharedFile;
