import React from 'react';
import { jwtDecode } from 'jwt-decode';


function GenerateKeys({ token }) {
    const handleGenerateKeys = async () => {
        try {
          const decodedToken = jwt_decode(token); // Ahora se usa correctamente
          const username = decodedToken.username; // Ajusta esto según el contenido del token

          const requestData = {
            username: username, // Usar el nombre de usuario extraído del token
          };

          const response = await fetch(`${process.env.REACT_APP_API_URL}/api/tasks/generate-keys`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestData),
          });

          if (!response.ok) {
            throw new Error('Error al generar las llaves');
          }

          const data = await response.blob(); // Obtener la respuesta en formato binario para la descarga
          // Si es necesario procesar el archivo, por ejemplo, para guardarlo, aquí iría el código correspondiente
        } catch (error) {
          console.error('Error:', error);
        }
      };

    return (
        <div>
            <h1>Generar Llaves RSA</h1>
            <button onClick={handleGenerateKeys}>Generar y Descargar Llaves</button>
        </div>
    );
}

export default GenerateKeys;
