import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

function SignSharedFile({ token }) {
    const [archivoId, setArchivoId] = useState('');
    const [privateKeyFile, setPrivateKeyFile] = useState(null);
    const [sharedFiles, setSharedFiles] = useState([]); // Archivos compartidos con el usuario
    const [username, setUsername] = useState('');

    useEffect(() => {
        const decodedToken = jwtDecode(token);
        setUsername(decodedToken.username);

        // Obtener los archivos compartidos
        const fetchSharedFiles = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_API_URL}/api/files/shared-files`, {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                    },
                });

                const files = await response.json();
                if (Array.isArray(files)) {
                    setSharedFiles(files);
                } else {
                    console.error('Error: La respuesta no es un arreglo.', files);
                    setSharedFiles([]);
                }
            } catch (error) {
                console.error('Error al obtener los archivos compartidos:', error);
                setSharedFiles([]);
            }
        };

        fetchSharedFiles();
    }, [token]);

    const handlePrivateKeyUpload = (e) => {
        setPrivateKeyFile(e.target.files[0]);  // Guardar el archivo .pem en el estado
    };

    const handleSignFile = async () => {
        if (!privateKeyFile || !archivoId) {
            alert('Por favor, selecciona un archivo y carga la clave privada.');
            return;
        }
    
        // Asegúrate de que archivoId sea un número
        const archivoIdInt = parseInt(archivoId, 10);
        if (isNaN(archivoIdInt)) {
            alert('ID de archivo inválido');
            return;
        }
    
        const formData = new FormData();
        formData.append('archivo_id', archivoIdInt);  // Enviar el ID como número
        formData.append('file', privateKeyFile);     // Subir el archivo .pem
    
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/files/sign-shared`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
                body: formData, // Enviar FormData que incluye el archivo
            });
    
            if (!response.ok) {
                throw new Error('Error al firmar el archivo');
            }
    
            const data = await response.json();
            alert(`Archivo firmado exitosamente. Firma: ${data.signature}`);
        } catch (error) {
            console.error('Error al firmar el archivo:', error);
            alert('Error al firmar el archivo');
        }
    };
    

    return (
        <div>
            <h2>Firmar Archivo Compartido</h2>
            <select value={archivoId} onChange={(e) => setArchivoId(e.target.value)}>
                <option value="">Selecciona un archivo</option>
                {sharedFiles.length > 0 ? (
                    sharedFiles.map((file) => (
                        <option key={file.id} value={file.id}>{file.nombre_archivo}</option>
                    ))
                ) : (
                    <option value="">No tienes archivos compartidos</option>
                )}
            </select>
            <input type="file" accept=".pem" onChange={handlePrivateKeyUpload} />
            <button onClick={handleSignFile}>Firmar Archivo</button>
        </div>
    );
}

export default SignSharedFile;
